mapper.update:
UPDATE user_tb
SET pw =
#{pw}, name = #{name}, birthDate=#{birthDate},
phoneNumber=#{phoneNumber}
WHERE id = #{id}

UPDATE user_tb
SET blockedNumber = blockedNumber +1
WHERE id = #{userId}

mapper.select:
SELECT blockedNumber
FROM user_tb
WHERE id = #{userId}

SELECT reportedNumber
FROM user_tb
WHERE id = #{userId}

SELECT accessBlockedDate
FROM user_tb
WHERE id = #{userId}

mapper.update:
UPDATE user_tb
SET accessBlockedDate = null
WHERE id = #{userId}

UPDATE user_tb
SET accessBlockedDate = SYSDATE
WHERE id = #{userId}
